SELECT '"' || subs_id || '"|"' || SUM(tot_vol) FROM IA.T_IA_APP_TYPE_CNT_VOL_D WHERE app_type=859 AND data_day IN ('20141130', '20141201', '20141202') AND subs_id NOT IN (SELECT DISTINCT(subs_id) FROM IA.T_IA_APP_TYPE_CNT_VOL_D WHERE app_type=859 and data_day < 20141130) GROUP BY subs_id HAVING SUM(tot_vol)>10240